// -----------------------------------------------------------
// 1. Clase base Animal (convertida a abstracta en el ejercicio 9)
// -----------------------------------------------------------
abstract class Animal {
  // Campo para el nombre
  String nombre;
  // 6. Campo privado para la edad
  int _edad = 0;

  // 1. Constructor
  Animal(this.nombre);

  // 9. M√©todo abstracto: sin implementaci√≥n en la clase base.
  void mover();

  // 6. Getter para _edad
  int get edad => _edad;

  // 6. Setter para _edad (impide valores negativos)
  set edad(int e) {
    if (e >= 0) {
      _edad = e;
    } else {
      print("Error: La edad no puede ser negativa.");
    }
  }

  // 8. Sobrescritura b√°sica de toString()
  @override
  String toString() => "Un $nombre de tipo ${runtimeType.toString()}";
}

// -----------------------------------------------------------
// 2. Subclases Mammal, Bird, Fish
// -----------------------------------------------------------
class Mammal extends Animal {
  // 3. Constructor que pasa 'nombre' al constructor de la clase base.
  Mammal(super.nombre);

  // 2. Redefinici√≥n obligatoria de mover()
  @override
  void mover() => print("$nombre (Mam√≠fero) camina sobre la tierra");
}

class Bird extends Animal {
  Bird(super.nombre);

  // 2. Redefinici√≥n obligatoria de mover()
  @override
  void mover() => print("$nombre (Ave) vuela por el aire");
}

class Fish extends Animal {
  Fish(super.nombre);

  // 2. Redefinici√≥n obligatoria de mover()
  @override
  void mover() => print("$nombre (Pez) nada en el agua");
}

// -----------------------------------------------------------
// 4. Mixins
// -----------------------------------------------------------
// Los mixins proporcionan una forma de reutilizar el c√≥digo de la clase
// en m√∫ltiples jerarqu√≠as de clases.
mixin Walk {
  void caminar() => print("Caminando...");
}

mixin Swim {
  void nadar() => print("Nadando...");
}

mixin Fly {
  void volar() => print("Volando...");
}

// -----------------------------------------------------------
// Clases concretas (ejercicios 4, 5, 8, 10)
// -----------------------------------------------------------

// Clase Cat (Mam√≠fero simple)
class Cat extends Mammal {
  Cat(super.nombre);

  // 8. Sobrescritura de toString()
  @override
  String toString() => "Soy un Gato llamado $nombre";
}

// 4. Bat: hereda de Mammal e implementa Walk y Fly.
class Bat extends Mammal with Walk, Fly {
  Bat(super.nombre);

  // 8. Sobrescritura de toString()
  @override
  String toString() => "Soy un Murci√©lago llamado $nombre que puede ${runtimeType.toString() is Fly ? 'volar' : ''} y ${runtimeType.toString() is Walk ? 'caminar' : ''}";
}

// 4. Dolphin: hereda de Mammal e implementa Swim.
class Dolphin extends Mammal with Swim {
  Dolphin(super.nombre);

  // Redefine mover para ser m√°s espec√≠fico
  @override
  void mover() => print("$nombre (Delf√≠n) nada en el mar.");

  // 8. Sobrescritura de toString()
  @override
  String toString() => "Soy un Delf√≠n llamado $nombre que nada";
}

// Clase Shark (Pez simple)
class Shark extends Fish {
  Shark(super.nombre);

  // 8. Sobrescritura de toString()
  @override
  String toString() => "Soy un Tibur√≥n llamado $nombre";
}

// FlyingFish: Pez que tambi√©n puede volar (ejercicio 10)
class FlyingFish extends Fish with Fly {
  FlyingFish(super.nombre);
}


// 5. Duck: hereda de Bird e implementa Walk, Swim y Fly.
class Duck extends Bird with Walk, Swim, Fly {
  Duck(super.nombre);

  // Redefine mover para ser m√°s espec√≠fico
  @override
  void mover() => print("$nombre (Pato) se mueve en tierra, aire y agua.");

  // 8. Sobrescritura de toString()
  @override
  String toString() => "Soy un Pato llamado $nombre con m√∫ltiples habilidades (volar, nadar, caminar)";
}

// -----------------------------------------------------------
// Funci√≥n principal (main)
// -----------------------------------------------------------
void main() {
  print("--- 1. 2. 3. 4. 5. Jerarqu√≠a de Clases y Mixins ---");
  // 3. Crea un Bird y un Fish e imprime su movimiento.
  var cigue√±a = Bird("Cig√ºe√±a");
  var salmon = Fish("Salm√≥n");
  cigue√±a.mover(); // L√≥gica: Usa el m√©todo mover() de Bird
  salmon.mover();  // L√≥gica: Usa el m√©todo mover() de Fish

  // 5. Instancia un Duck y llama a sus tres comportamientos.
  var patoDonald = Duck("Donald");
  print("\nComportamientos de ${patoDonald.nombre}:");
  patoDonald.caminar(); // L√≥gica: M√©todo del mixin Walk
  patoDonald.nadar();   // L√≥gica: M√©todo del mixin Swim
  patoDonald.volar();   // L√≥gica: M√©todo del mixin Fly

  print("\n--- 6. Encapsulaci√≥n y Getters/Setters ---");
  // 6. Prueba el setter de Animal
  patoDonald.edad = 5; // L√≥gica: Asigna 5 a _edad a trav√©s del setter
  print("${patoDonald.nombre} tiene ${patoDonald.edad} a√±os.");
  patoDonald.edad = -2; // L√≥gica: El setter impide la asignaci√≥n y muestra el error.
  print("Tras intento inv√°lido, ${patoDonald.nombre} sigue teniendo ${patoDonald.edad} a√±os.");

  print("\n--- 7. Polimorfismo ---");
  // 7. Lista de animales heterog√©nea (Polimorfismo)
  List<Animal> animales = [
    Cat("Pelusa"),
    patoDonald, // El pato ya instanciado
    Shark("Tibur√≥n Blanco"),
    Bat("Dr√°cula"),
    Dolphin("Flipper"),
  ];

  // Recorre la lista y llama a mover(). Cada objeto usa su implementaci√≥n.
  for (var a in animales) {
    a.mover(); // L√≥gica: El m√©todo llamado se determina en tiempo de ejecuci√≥n.
  }

  print("\n--- 8. Sobrescritura de toString() ---");
  // 8. Imprime la descripci√≥n de algunos objetos
  print(animales[0]); // L√≥gica: Usa el toString() reescrito de Cat
  print(animales[3]); // L√≥gica: Usa el toString() reescrito de Bat

  print("\n--- 10. Ejercicio Integrador ‚Äì Zool√≥gico ---");
  // 10. Lista integradora con FlyingFish
  List<Animal> zoologico = [
    Cat("Garfield"),
    Dolphin("Luna"),
    Duck("Cuack"),
    Bat("Chiro"),
    Shark("Bruce"),
    FlyingFish("Icarus"),
  ];

  for (var animal in zoologico) {
    print("\n${animal.nombre} (${animal.runtimeType}):");
    // L√≥gica: Usa el operador 'is' para verificar si el objeto tiene la capacidad
    // proporcionada por el mixin (comprobaci√≥n de tipo en tiempo de ejecuci√≥n).
    if (animal is Walk) animal.caminar();
    if (animal is Swim) animal.nadar();
    if (animal is Fly) animal.volar();
    animal.mover(); // Muestra el movimiento por defecto (polimorfismo)
  }
}
